targets.expr:
sum by (client_names, client_ip) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [$__range]
    )
)

sum by (client_names, client_ip) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" | response_type = "BLOCKED" [$__range]
    )
)

sum by (question_name) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [$__range]
    )
)

sum by (question_name) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" | response_type = "BLOCKED" [$__range]
    )
)

sum by (client_names) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [5m]
    )
)

sum by (response_type) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [5m]
    )
)

sum by (client_names) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [$__range]
    )
)

sum by (question_type) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [$__range]
    )
)

sum by (response_type) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [$__range]
    )
)

sum by (response_code) (
    count_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" [$__range]
    )
)

sum by (upstream) (
    count_over_time(
        {app="blocky"} | regexp `"response_reason":".*\s+\((?P<upstream>\S+)\)` | json | prefix = "queryLog" | client_names =~ "$client" | upstream != "" [$__range]
    )
)

avg_over_time(
    {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" | response_type = "RESOLVED" | unwrap duration_ms | __error__ = "" [1s]
)

max by (client_names) (
    max_over_time(
        {app="blocky"} | json | prefix = "queryLog" | client_names =~ "$client" | response_type = "RESOLVED" | unwrap duration_ms | __error__ = "" [1m]
    )
)
